CREATE TABLE IF NOT EXISTS customer(id int PRIMARY KEY AUTO_INCREMENT, name VARCHAR(255), email VARCHAR(255), password VARCHAR(255), address VARCHAR(255), tel VARCHAR(255), role VARCHAR(255) DEFAULT 'ROLE_USER');
CREATE TABLE IF NOT EXISTS post(id int PRIMARY KEY AUTO_INCREMENT, customer_id int, title VARCHAR(255), datetime DATETIME, contents VARCHAR(255), CONSTRAINT fk_customer_id FOREIGN KEY(customer_id) REFERENCES customer (id) ON DELETE CASCADE ON UPDATE CASCADE);
CREATE TABLE IF NOT EXISTS todo(id int PRIMARY KEY AUTO_INCREMENT, post_id int, todo VARCHAR(255), CONSTRAINT fk_post_id FOREIGN KEY(post_id) REFERENCES post (id) ON DELETE CASCADE ON UPDATE CASCADE);